mapper.select:
Select * From ( Select rownum rn, a.* From ( Select b.*, m.m_nickname From Board b, Member m Where b.b_re_level = 0 and b.member_id = m.member_id
order by b.b_view_cnt desc
order by b.b_like_cnt desc
order by b.create_date desc
) a ) Where rn between #{startRow} and #{endRow}

Select *
From
(   Select   rownum rn, a.*
   From   (   Select   b.*, m.m_nickname
            From   Board b, Member m
            Where   b.b_re_level = 0
            and      b.member_id = m.member_id
            and      b.b_common_board = #{b_common_board}
            order by b.create_date desc
         ) a
)
Where rn between #{startRow} and #{endRow}

mapper.update:
Update   Board
Set      b_view_cnt = b_view_cnt + 1
Where   board_id = #{board_id}
