mapper.update:
UPDATE  G_WRITING 
SET 
 G_REPLY_YN   	   =  '1',	
 G_REPLY_CONTENT  = #{g_reply_content} 

WHERE  G_WRITING_ID = #{g_writing_id}

mapper.select:
 		SELECT *
		FROM
		(
		        SELECT rownum rn , a.*
		        FROM
		                   (select * from G_WRITING 
						     where G_COMMON_CSBOARD = #{code}        
		                    order by g_writing_id desc) a
		 )   
 		WHERE  rn BETWEEN #{start} and #{end}

SELECT   COUNT(*)
FROM   G_WRITING
WHERE   member_id = #{memberId}
