mapper.insert:
INSERT INTO Res_Img (	  restaurant_id
						, img_id
						, img_original_file
						, img_stored_file
						)
VALUES (				#{restaurant_id},
						Res_Img_SEQ.nextval,						 		
   						#{img_original_file	, jdbcType=VARCHAR},
    					#{img_stored_file}
     				)

mapper.delete:
delete from Res_Img 
where IMG_ID = #{img_id}
and   RESTAURANT_ID = #{restaurant_id}
